mapper.select:
SELECT a.yjdid, a.yjdbh, a.yjsj, a.yjr, a.jsr, a.yjzt FROM bdc_yjd a WHERE EXISTS ( SELECT t1.yjdid FROM bdc_xm_yjd_gx t1
left JOIN bdc_xm x ON t1.xmid = x.xmid left join bdc_qlr q on t1.xmid = q.xmid
inner JOIN bdc_xm x ON t1.xmid = x.xmid inner join bdc_qlr q on t1.xmid = q.xmid
WHERE a.yjdid = t1.yjdid
and q.qlrlb=1
AND x.qxdm = #{qxdm}
AND x.slbh = #{slbh}
AND t1.xmid in #{xmid}
AND q.qlrmc like '%' || #{qlr, jdbcType = VARCHAR} || '%'
AND X.XMID IN ( SELECT XMID FROM BDC_XM_ZS_GX WHERE ZSID IN ( SELECT ZSID FROM BDC_ZS Z WHERE z.bdcqzh like '%' || #{bdcqzh,jdbcType=VARCHAR} || '%' ) )
AND instr(a.yjdbh, #{yjdbh})>0
AND instr(a.yjr,#{yjr})>0
AND a.yjzt = #{yjzt,jdbcType=INTEGER}
AND a.yjsj >= to_date(#{qssj,jdbcType=DATE},'yyyy-MM-dd')
AND a.yjsj <= to_date(#{jzsj,jdbcType=DATE},'yyyy-MM-dd')
AND to_char(a.yjsj,'yyyy-MM-dd') = #{yjsj}
)
order by a.yjsj desc
ORDER BY a.yjdbh asc

SELECT DISTINCT t.xmid AS xmid, t.slbh FROM bdc_xm t INNER JOIN bdc_xm_yjd_gx t1 ON t.xmid = t1.xmid
AND t1.xmid IN
#{xmid}

select.if:
SELECT COUNT(t.yjdbh) AS result
FROM bdc_yjd t
WHERE substr(t.yjdbh, 0, 8) = #{_parameter}

mapper.select:
select * from BDC_YJD_TASK_GX g


        and g.taskid = #{taskid}


        and g.yjdh = #{yjdh}


order by g.yjdh desc
