create table BDC_XT_BB
(
    BBID VARCHAR2(32) NOT NULL,
    BBBH VARCHAR2(50) NOT NULL,
    FBRID VARCHAR2(32),
    FBR VARCHAR2(50),
    FBSJ DATE         NOT NULL,
    constraint BDC_XT_BB_PK primary key(BBID)
);
comment on table BDC_XT_BB is '不动产登记业务管理系统版本表';
comment on column BDC_XT_BB.BBID
is '版本ID';
comment on column BDC_XT_BB.BBBH
is '版本编号';
comment on column BDC_XT_BB.FBRID
    is '发布人ID';
comment on column BDC_XT_BB.FBR
    is '发布人';
comment on column BDC_XT_BB.FBSJ
    is '发布时间';

