ALTER TABLE BDC_BDCDJB_ZDJBXX ADD ZDYT2 VARCHAR2(5);
ALTER TABLE BDC_BDCDJB_ZDJBXX ADD ZDYT3 VARCHAR2(5);
ALTER TABLE BDC_BDCDJB_ZDJBXX ADD TDSYQSSJ DATE;
ALTER TABLE BDC_BDCDJB_ZDJBXX ADD TDSYQSSJ2 DATE;
ALTER TABLE BDC_BDCDJB_ZDJBXX ADD TDSYQSSJ3 DATE;
ALTER TABLE BDC_BDCDJB_ZDJBXX ADD TDSYJSSJ DATE;
ALTER TABLE BDC_BDCDJB_ZDJBXX ADD TDSYJSSJ2 DATE;
ALTER TABLE BDC_BDCDJB_ZDJBXX ADD TDSYJSSJ3 DATE;
update BDC_BDCDJB_ZDJBXX a set (a.zdyt2,a.zdyt3)=(select b.zdzhyt2,b.zdzhyt3 from bdc_xm b where
a.zddm=substr(b.bdcdyh,0,19) and rownum=1);
update BDC_BDCDJB_ZDJBXX a set (a.tdsyqssj,a.tdsyjssj)=(select b.tdsyqssj,b.tdsyjssj from bdc_fdcq b where
a.zddm=substr(b.bdcdyh,0,19) and rownum=1);

