CREATE TABLE BDC_FPH (
FPHID VARCHAR2(32 BYTE) NOT NULL ,
NF VARCHAR2(4 BYTE) ,
QXDM VARCHAR2(6 BYTE) ,
FPH VARCHAR2(100 BYTE) NOT NULL ,
SYQK NUMBER ,
CJR VARCHAR2(50 BYTE) ,
CJRID VARCHAR2(32 BYTE) ,
CJSJ DATE ,
SFXXID VARCHAR2(32 BYTE) ,
BZ VARCHAR2(400 BYTE) ,
LQR VARCHAR2(50 BYTE) ,
LQRID VARCHAR2(32 BYTE) ,
LQBM VARCHAR2(50 BYTE) ,
LQBMID VARCHAR2(32 BYTE) ,
FPHSYMXID VARCHAR2(32 BYTE) ,
SLBH VARCHAR2(50 BYTE)
);
COMMENT ON COLUMN BDC_FPH.FPHID IS '主键';
COMMENT ON COLUMN BDC_FPH.NF IS '年份';
COMMENT ON COLUMN BDC_FPH.QXDM IS '区县代码';
COMMENT ON COLUMN BDC_FPH.FPH IS '发票号 唯一约束';
COMMENT ON COLUMN BDC_FPH.SYQK IS '使用情况';
COMMENT ON COLUMN BDC_FPH.CJR IS '创建人';
COMMENT ON COLUMN BDC_FPH.CJRID IS '创建人ID';
COMMENT ON COLUMN BDC_FPH.CJSJ IS '创建时间';
COMMENT ON COLUMN BDC_FPH.SFXXID IS '收费信息ID';
COMMENT ON COLUMN BDC_FPH.BZ IS '备注';
COMMENT ON COLUMN BDC_FPH.LQR IS '领取人';
COMMENT ON COLUMN BDC_FPH.LQRID IS '领取人ID';
COMMENT ON COLUMN BDC_FPH.LQBM IS '领取部门';
COMMENT ON COLUMN BDC_FPH.LQBMID IS '领取部门id';
COMMENT ON COLUMN BDC_FPH.FPHSYMXID IS '使用明细id';
COMMENT ON COLUMN BDC_FPH.SLBH IS '受理编号';
COMMENT ON TABLE  BDC_FPH IS '不动产发票号管理';

