"JKXFF" VARCHAR2(100 BYTE),
"SFJLRZ" NUMBER DEFAULT 0,
"CJSJ" DATE,
"CJR" VARCHAR2(50 BYTE),
"XGSJ" DATE,
"XGR" VARCHAR2(50 BYTE),
"SFSC" NUMBER DEFAULT 0  NOT NULL
);
