ALTER TABLE BDC_XMFB ADD SYQX_TEMP NUMBER(4);
--step2：将要修改的字段的值存入缓冲区，同时将要修改的字段制空
UPDATE BDC_XMFB set SYQX_TEMP=SYQX,SYQX=null;
--step3：修改字段类型
ALTER TABLE BDC_XMFB modify SYQX VARCHAR2(200);
--step4：转移数据，同时制空缓冲区字段
UPDATE BDC_XMFB set SYQX=TO_CHAR(SYQX_TEMP),SYQX_TEMP=NULL;
--step5：删除缓冲区字段
ALTER TABLE BDC_XMFB DROP COLUMN SYQX_TEMP;

