AND x.SLBH = #{transtionId}
GROUP BY x.XMID ) qlr ON xm.XMID = qlr.XMID LEFT JOIN (SELECT x.xmid,TO_CHAR( WMSYS.WM_CONCAT ( l.LZRDH ) ) AS LZRDH,TO_CHAR( WMSYS.WM_CONCAT ( l.LZRZJZL ) ) AS LZRZJZL,TO_CHAR( WMSYS.WM_CONCAT ( l.LZRZJH ) ) AS LZRZJH FROM BDC_lzr l LEFT JOIN BDC_XM x ON x.XMID = l.xmid WHERE
x.SLBH = #{transtionId}
GROUP BY x.XMID) LZR ON LZR.xmid = xm.xmid INNER JOIN BDC_ZD_GYFS ZD ON Z.GYFS = ZD.DM WHERE 1=1
AND XM.SLBH = #{transtionId}
AND qlr.qlrmc like '%'|| #{qlr} || '%'
AND xm.bdcdyh = #{bdcdyh}
