mapper.select:
SELECT
c.szsxqc mc,
a.total_count,a.zs_count,a.zms_count,a.scdjz_count FROM (SELECT
y.dwdm,
COUNT(*) total_count, SUM(CASE WHEN y.zstype='不动产权' THEN 1 ELSE 0 END) AS zs_count, SUM(CASE WHEN y.zstype='不动产证明' THEN 1 ELSE 0 END) AS zms_count, SUM(CASE WHEN y.zstype='首次登记证' THEN 1 ELSE 0 END) AS scdjz_count FROM bdc_dzzz_ywxx y LEFT JOIN bdc_dzzz_zzxx t ON t.zzid = y.zzid
AND t.zzbfrq >= #{cjsjqssj,jdbcType=TIMESTAMP}
AND t.zzbfrq <= #{cjsjjssj,jdbcType=TIMESTAMP}
GROUP BY y.dwdm
) a
LEFT JOIN bdc_dzzz_config c ON a.dwdm = c.dwdm

SELECT a.zzid AS "zzid",a.czzt AS "czzt",b.ywr "ywr", a.bdcqzh AS "bdcqzh", a.zzqzlj AS "zzqzlj", b.zl AS "zl", b.bdcdyh AS "bdcdyh",c.szsxqc AS "szsxqc" , a.zzmc AS "zzmc", a.zzbs AS "zzbs", a.zzbfjg AS "zzbfjg", a.zzbgyy AS "zzbgyy", a.zzyxqjzrq AS "zzyxqjzrq" , a.zzzt AS "zzzt", a.qzzt AS "qzzt" FROM bdc_dzzz_zzxx a LEFT JOIN bdc_dzzz_ywxx b on a.zzid = b.zzid LEFT JOIN bdc_dzzz_config c on b.dwdm = c.dwdm
AND b.dwdm like #{dwdm}||'%'
AND a.zzlxdm =#{zzlxdm}
AND b.bdcdyh LIKE '%' || #{bdcdyh} || '%'
AND a.bdcqzh LIKE '%' || #{bdcqzh} || '%'
AND a.czzt LIKE '%' || #{czzt} || '%'
AND b.ywr LIKE '%' || #{ywr} || '%'
AND a.zzbgyy ='2'
AND (a.zzbgyy ='1'or a.zzbgyy is null)
AND a.cjsj >= #{cjsjqssj,jdbcType=TIMESTAMP}
AND a.cjsj <= #{cjsjjssj,jdbcType=TIMESTAMP}
AND a.qzzt = #{qzzt}
AND (
b.dwdm = #{item}
)
ORDER BY a.cjsj
