mapper.select:
SELECT x.djlx nationalywdm FROM bdc_xm x
AND substr(x.bdcdyh,0,6)=#{qxdm}
AND trunc(x.djsj) = trunc(#{accessDate})

SELECT x.djlx nationalywdm FROM bdc_xm x
AND x.qxdm=#{qxdm}
AND trunc(x.djsj) = trunc(#{accessDate})

SELECT x.xmid ywh, x.djlx, x.qllx, x.bdcdyh -- a.ywbwid bwid FROM bdc_xm x LEFT JOIN bdc_jr_sjjl a ON a.ywlsh = x.xmid
AND substr(x.bdcdyh,0,6)=#{qxdm}
AND trunc(x.djsj) = trunc(#{accessDate})

SELECT COUNT(t.ywdm) num, l.djlx FROM ( SELECT case x.djlx when 901 then 900 when 902 then 900 else x.djlx end as ywdm FROM ${logTable} a left join bdc_xm x on x.XMID=a.ywlsh --WHERE a.cgbs in (0,1,2)
a.ywdm is not null
AND a.cgbs in
#{item}
AND substr(a.bdcdyh,0,6)=#{qxdm}
AND trunc(a.sjjrrq) = trunc(#{accessDate})
) t RIGHT JOIN ( SELECT (LEVEL-1)||'00' djlx FROM dual CONNECT BY LEVEL < 11 ) l ON l.djlx = t.ywdm GROUP BY l.djlx ORDER BY l.djlx

SELECT COUNT(t.ywdm) num, l.djlx FROM ( --区分抵押权和地役权，地役权仍为9000102，抵押权9000101用0000101代替 SELECT a.ywdm FROM ${logTable} a left join bdc_xm b on b.xmid = a.ywlsh WHERE a.cgbs IN ( 0, 1, 2 )
AND b.qxdm=#{qxdm}
AND trunc(a.sjjrrq) = trunc(#{accessDate})
) t RIGHT JOIN ( SELECT (LEVEL-1)||'00' djlx FROM dual CONNECT BY LEVEL < 11 ) l ON l.djlx = substr(t.ywdm,0,3) GROUP BY l.djlx ORDER BY l.djlx

SELECT a.ywlsh ywh, a.ywbwid bwid, a.bdcdyh FROM ${logTable} a
AND a.cgbs in
#{item}
AND substr(a.bdcdyh,0,6)=#{qxdm}
AND trunc(a.sjjrrq) = trunc(#{accessDate})

SELECT a.ywlsh ywh, a.ywbwid bwid, a.bdcdyh FROM ${logTable} a left join bdc_xm b on b.xmid = a.ywlsh WHERE a.cgbs IN ( 0, 1, 2 )
AND b.qxdm=#{qxdm}
AND trunc(a.sjjrrq) = trunc(#{accessDate})
